mapper.select:
select
from xy_group LEFT JOIN xy_role ON xy_group.role_id= xy_role.id LEFT JOIN xy_menu ON xy_group.menu_id = xy_menu.id where 1=1
and xy_group.id=#{id,jdbcType=INTEGER}
and xy_group.role_id=#{roleId,jdbcType=INTEGER}
and xy_group.menu_id=#{menuId,jdbcType=BIGINT}
and xy_group.is_delete = 0 and xy_role.is_delete = 0 and xy_menu.is_delete = 0

select

from xy_group
where 1=1

    and id=#{id,jdbcType=INTEGER}


    and role_id=#{roleId,jdbcType=INTEGER}


    and menu_id=#{menuId,jdbcType=BIGINT}


    and isDelete=#{isDelete,jdbcType=CHAR}


    and createBy=#{createBy,jdbcType=VARCHAR}


    and createAt=#{createAt,jdbcType=TIMESTAMP}


    and updateBy=#{updateBy,jdbcType=VARCHAR}


    and updateAt=#{updateAt,jdbcType=TIMESTAMP}

mapper.update:
update xy_group

    is_delete = 1, update_by = #{updateBy,jdbcType=VARCHAR},update_at = #{updateAt,jdbcType=TIMESTAMP}

where role_id = #{roleId,jdbcType=INTEGER}
